// === Font class implementation ===
/*!
	@brief init the OLED  font class object constructor
 */
display_Fonts::display_Fonts()
{
	setFont(font_default);
};

/*!
	@brief SetFont 
	@param FontChoice SelectedFontName Select this font, pass the font enum name
	@return	Will return rdlib::Return_Codes_e  enum
		-# Pass rdlib::Success
		-# Error 1 rdlib::WrongFont
		-# Error 2 rdlib::FontPtrNullptr
 */
rdlib::Return_Codes_e display_Fonts::setFont(display_Font_name_e FontChoice) {

	std::span<const uint8_t> SelectedFontName;
	switch(FontChoice)
	{
		case font_default: SelectedFontName = pFontDefault; break;
		case font_pico : SelectedFontName =  pFontPico; break;
		case font_sevenSeg : SelectedFontName =  pFontSevenSeg; break;
		case font_groTeskBig : SelectedFontName = pFontGroTeskBig; break;
		case font_gll : SelectedFontName = pFontGll; break;
		case font_sinclairS : SelectedFontName = pFontSinclairS; break;
		case font_orla : SelectedFontName =  pFontOrla; break;
		case font_retro : SelectedFontName =  pFontRetro; break;
		case font_mega : SelectedFontName =  pFontMega; break;
		case font_arialBold : SelectedFontName = pFontArialBold; break;
		case font_hallfetica : SelectedFontName = pFontHallfetica; break;
		case font_arialRound : SelectedFontName = pFontArialRound; break;
		case font_groTesk : SelectedFontName =  pFontGroTesk; break;
		case font_mint : SelectedFontName =  pFontMint; break;
		case font_inconsola: SelectedFontName =  pFontInconsola; break;
		case font_sixteenSeg : SelectedFontName = pFontSixteenSeg; break;
		default: 
			fprintf(stderr, "Error 1: setFont: Invalid font choice \n");
			return rdlib::WrongFont;
		break;
	}

	if (SelectedFontName.empty())
	{
		fprintf(stderr,"Error 2: setFontX: Error: Font data is empty \r\n");
		return rdlib::FontDataEmpty;
	}

	_FontSelect   = SelectedFontName;
	_Font_X_Size  = SelectedFontName[0];
	_Font_Y_Size  = SelectedFontName[1];
	_FontOffset   = SelectedFontName[2];
	_FontNumChars = SelectedFontName[3];
	_FontInverted = false;

	return rdlib::Success;
}

/*!
	@brief setInvertFont
	@param invertStatus set the invert status flag of font ,false = off.
*/
void display_Fonts::setInvertFont(bool invertStatus) {
	 _FontInverted = invertStatus;
}

/*!
	@brief getInvertFont
	@return invert status flag of font ,false = off.
*/
bool display_Fonts::getInvertFont()
{ return _FontInverted; }

